mapper.select:
SELECT COUNT(*) FROM VIEW_TEAM_DRAFT WHERE 1=1
AND fk_department_no = #{fk_department_no}
AND LOWER(${searchType}) LIKE '%'||LOWER(#{searchWord})||'%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNO FROM ( SELECT * FROM VIEW_TEAM_DRAFT WHERE 1=1
AND fk_department_no = #{fk_department_no}
AND LOWER(${searchType}) LIKE '%'||LOWER(#{searchWord})||'%'
ORDER BY ${sortType} ${sortOrder} NULLS LAST )A ) WHERE RNO BETWEEN #{startRno} AND #{endRno}

SELECT COUNT(*) FROM VIEW_DRAFT_SENT
